create table alerts (id bigint not null auto_increment, location_id bigint, city_name varchar(255), description varchar(2000), effective_local varchar(255), effective_utc varchar(255), expires_local varchar(255), expires_utc varchar(255), severity varchar(255), title varchar(255), uri varchar(255), primary key (id)) engine=InnoDB;
create table current_weather (air_quality_index float(53) not null, apparent_temperature float(53) not null, cloud_cover integer not null, humidity integer not null, precipitation_in_last_hour float(53) not null, pressure integer not null, snow_fall_in_last_hour float(53) not null, temperature float(53) not null, wind_speed float(53) not null, id bigint not null auto_increment, location_id bigint, city_name varchar(255), date_time varchar(255), observation_time varchar(255), sunrise varchar(255), sunset varchar(255), wind_direction varchar(255), primary key (id)) engine=InnoDB;
create table forcast (app_max_temp float(53) not null, app_min_temp float(53) not null, clouds integer not null, dewpt float(53) not null, max_temp float(53) not null, min_temp float(53) not null, moon_phase float(53) not null, moonrise_ts integer not null, moonset_ts integer not null, pop float(53) not null, precip float(53) not null, pres float(53) not null, rh integer not null, slp float(53) not null, snow float(53) not null, snow_depth float(53), sunrise_ts integer not null, sunset_ts integer not null, temp float(53) not null, ts float(53) not null, uv float(53) not null, vis float(53) not null, wind_dir integer not null, wind_spd float(53) not null, id bigint not null auto_increment, location_id bigint, city_name varchar(255), datetime varchar(255), wind_cdir varchar(255), wind_cdir_full varchar(255), primary key (id)) engine=InnoDB;
create table location (city_id integer not null, lat float(53) not null, lon float(53) not null, id bigint not null auto_increment, city_name varchar(255), country_code varchar(255), state_code varchar(255), primary key (id)) engine=InnoDB;
